import React, { useState, useEffect } from 'react';
import axios from 'axios';

// The base URL for your backend API.
const API_BASE_URL = 'http://127.0.0.1:5000/api';

const App = () => {
  // State variables
  const [tasks, setTasks] = useState([]);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [formTask, setFormTask] = useState({ id: null, title: '', description: '' });
  const [isEditing, setIsEditing] = useState(false);
  const [error, setError] = useState('');

  // --- Auth & Token Handling ---

  // Centralized function to get authentication headers
  const getAuthHeaders = () => {
    const token = localStorage.getItem('access_token');
    return {
      headers: {
        Authorization: `Bearer ${token}`
      }
    };
  };

  // Check for an existing token on initial load
  useEffect(() => {
    const token = localStorage.getItem('access_token');
    if (token) {
      setIsLoggedIn(true);
      fetchTasks(); // ✨ FIX: No longer needs a token passed here
    }
  }, []);

  const handleAuthSubmit = async (type) => {
    setError('');
    try {
      const endpoint = type === 'login' ? '/login' : '/register';
      const response = await axios.post(`${API_BASE_URL}${endpoint}`, { username, password });

      if (type === 'login') {
        const token = response.data.access_token;
        localStorage.setItem('access_token', token);
        setIsLoggedIn(true);
        fetchTasks(); // ✨ FIX: No longer needs a token passed here
      } else { // 'register'
        alert('Registration successful! Please log in.');
        setUsername('');
        setPassword('');
      }
    } catch (err) {
      setError(err.response?.data?.msg || 'An unknown error occurred.');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('access_token');
    setIsLoggedIn(false);
    setTasks([]);
  };

  // --- Task Management ---

  // ✨ FIX: This function now uses the centralized getAuthHeaders function
  const fetchTasks = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/tasks`, getAuthHeaders());
      setTasks(response.data);
    } catch {
      setError('Failed to fetch tasks.');
      // If fetching fails, it could be an expired token, so log the user out.
      handleLogout();
    }
  };

  const handleTaskSubmit = async (e) => {
    e.preventDefault();
    if (!localStorage.getItem('access_token')) return;

    try {
      if (isEditing) {
        await axios.put(`${API_BASE_URL}/tasks/${formTask.id}`, formTask, getAuthHeaders());
        fetchTasks(); // ✨ FIX: No longer needs a token passed here
      } else {
        const response = await axios.post(`${API_BASE_URL}/tasks`, formTask, getAuthHeaders());
        const newTask = response.data;
        setTasks([newTask, ...tasks]);
      }
      setFormTask({ id: null, title: '', description: '' });
      setIsEditing(false);
    } catch {
      setError('Failed to save task.');
    }
  };

  const editTask = (task) => {
    setFormTask(task);
    setIsEditing(true);
  };

  const deleteTask = async (taskId) => {
    try {
      await axios.delete(`${API_BASE_URL}/tasks/${taskId}`, getAuthHeaders());
      // ✨ FIX: Update the task list by filtering out the deleted task
      setTasks(tasks.filter(task => task.id !== taskId));
    } catch {
      setError('Failed to delete task.');
    }
  };

  const toggleTaskStatus = async (taskId) => {
    try {
      await axios.put(`${API_BASE_URL}/tasks/${taskId}/toggle`, {}, getAuthHeaders());
      // ✨ FIX: Update the specific task in the list without a full refetch
      setTasks(tasks.map(task => 
        task.id === taskId ? { ...task, completed: !task.completed } : task
      ));
    } catch {
      setError('Failed to toggle status.');
    }
  };

  // --- Conditional Rendering ---

  // Show Login/Register view if not logged in
  if (!isLoggedIn) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-slate-100">
        <div className="w-full max-w-md p-8 space-y-6 bg-white rounded-xl shadow-lg">
          <h1 className="text-3xl font-bold text-center text-gray-900">Task Manager</h1>
          <div>
            <input 
              type="text" 
              placeholder="Username" 
              value={username} 
              onChange={(e) => setUsername(e.target.value)} 
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />
          </div>
          <div>
            <input 
              type="password" 
              placeholder="Password" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />
          </div>
          {error && <p className="text-sm text-center text-red-600">{error}</p>}
          <div className="flex items-center justify-between">
            <button onClick={() => handleAuthSubmit('login')} className="w-full px-6 py-3 font-semibold text-white bg-indigo-600 rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500">Log In</button>
            <span className="mx-4 text-gray-400">or</span>
            <button onClick={() => handleAuthSubmit('register')} className="w-full px-6 py-3 font-semibold text-gray-800 bg-gray-200 rounded-lg hover:bg-gray-300 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-400">Sign Up</button>
          </div>
        </div>
      </div>
    );
  }

  // Show Dashboard view if logged in
  return (
    <div className="min-h-screen bg-slate-100 p-4 sm:p-8">
      <div className="w-full max-w-3xl mx-auto">
        <header className="flex justify-between items-center mb-8">
          <h1 className="text-4xl font-extrabold text-gray-900">Your Tasks</h1>
          <button onClick={handleLogout} className="px-4 py-2 font-semibold text-gray-800 bg-gray-200 rounded-full hover:bg-gray-300 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-400">Log Out</button>
        </header>

        <div className="p-6 mb-8 space-y-4 bg-white rounded-xl shadow-lg">
          <h2 className="text-2xl font-bold text-gray-900">{isEditing ? 'Edit Task' : 'Add New Task'}</h2>
          <form onSubmit={handleTaskSubmit} className="space-y-4">
            <input 
              type="text" 
              placeholder="Task Title" 
              value={formTask.title} 
              onChange={(e) => setFormTask({ ...formTask, title: e.target.value })} 
              required 
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500" 
            />
            <textarea 
              placeholder="Description (optional)" 
              value={formTask.description} 
              onChange={(e) => setFormTask({ ...formTask, description: e.target.value })} 
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500" 
            />
            <div className="flex gap-4">
              <button type="submit" className="w-full px-6 py-3 font-semibold text-white bg-indigo-600 rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500">{isEditing ? 'Save Changes' : 'Add Task'}</button>
              {isEditing && <button type="button" onClick={() => { setIsEditing(false); setFormTask({ id: null, title: '', description: '' }); }} className="w-full px-6 py-3 font-semibold text-gray-800 bg-gray-200 rounded-lg hover:bg-gray-300 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-400">Cancel</button>}
            </div>
          </form>
        </div>

        <div className="p-6 bg-white rounded-xl shadow-lg">
          <ul className="space-y-4">
            {tasks.length > 0 ? (
              tasks.map(task => (
                <li key={task.id} className="flex items-center justify-between p-4 transition-all duration-200 bg-gray-50 rounded-lg hover:shadow-md hover:bg-gray-100">
                  <div className="flex items-center flex-grow min-w-0">
                    <input
                      type="checkbox"
                      checked={task.completed}
                      onChange={() => toggleTaskStatus(task.id)}
                      className="w-6 h-6 text-indigo-600 border-gray-300 rounded-full cursor-pointer focus:ring-indigo-500"
                    />
                    <div className="flex-1 min-w-0 ml-4">
                      <h3 className={`font-bold truncate ${task.completed ? 'line-through text-gray-500' : 'text-gray-900'}`}>{task.title}</h3>
                      <p className={`text-sm text-gray-600 truncate ${task.completed ? 'line-through' : ''}`}>{task.description || 'No description'}</p>
                    </div>
                  </div>
                  <div className="flex flex-shrink-0 gap-2 ml-4">
                    <button onClick={() => editTask(task)} className="px-3 py-1 text-sm font-medium text-blue-700 bg-blue-100 rounded-full hover:bg-blue-200">Edit</button>
                    <button onClick={() => deleteTask(task.id)} className="px-3 py-1 text-sm font-medium text-red-700 bg-red-100 rounded-full hover:bg-red-200">Delete</button>
                  </div>
                </li>
              ))
            ) : (
              <p className="text-center text-gray-500">No tasks yet. Add one above to get started!</p>
            )}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default App;
